mapper.update:
update tbl_order set
	dlvr_no = null, 
	order_state = '3-001'
where order_no = #{order_no}
	and dlvr_no = #{dlvr_no}

update tbl_order set
	order_state = '3-003'
where order_no = #{order_no}
	and dlvr_no = #{dlvr_no}

mapper.select:
select * from tbl_order
where user_no = #{user_no}
	and order_state = '3-002'
