mapper.select:
select * from tbl_order
where dlvr_no = #{dlvr_no}
and order_state = '100-002'

mapper.update:
update tbl_order set
	dlvr_no = null, 
	order_state = '100-001'
where order_no = #{order_no}
and dlvr_no = #{dlvr_no}

update tbl_order set
	order_state = '100-003'
where order_no = #{order_no}
and dlvr_no = #{dlvr_no}

mapper.select:
select * from tbl_order
where dlvr_no = #{dlvr_no}
and order_state = '100-003'
