mapper.select:
select t.*, d.dlvr_name, a.acc_name writer_name, a.acc_img writer_img, nvl(l.time_like, 0) time_like, nvl(l2.account_no, -1) liked_no from tbl_timeline t left outer join tbl_deliver d on t.dlvr_no = d.dlvr_no left outer join view_all a on t.writer_no = a.acc_no left outer join ( select time_no, count(*) time_like from tbl_like group by time_no) l on t.time_no = l.time_no left outer join ( select * from tbl_like where account_no = #{account_no}) l2 on t.time_no = l2.time_no where time_state != '2-000'
and time_state = #{searchType}
order by t.time_no desc

mapper.update:
update tbl_timeline set
time_content = #{time_content}
where time_no = #{time_no}

update tbl_timeline set
time_state = '2-000'
where time_no = #{time_no}
